mapper.select:
 		SELECT
    		count(*) cnt
		FROM
    		IE_SB_LIKEHATE, IE_MEMBER
		WHERE
		    m_no = sblh_mno
		    and m_id= #{mid}
		    and sblh_bno= #{bno}
		    and sblh_isshow= #{isshow}

 		SELECT
    		count(*) cnt
		FROM
    		IE_SB_LIKEHATE, IE_MEMBER
		WHERE
		    m_no = sblh_mno
		    and m_id= #{mid}
		    and sblh_bno= #{bno}
		    and sblh_isshow= #{isshow}

mapper.update:
UPDATE IE_STORY_BOARD
sb_title = #{title}, sb_content = #{content},
AND sb_no = #{bno}
